import React, {useState, useEffect} from "react";
import {listDecks} from "../utils/api"
import {Link} from "react-router-dom";
import DisplayDecks from "../home/DisplayDecks";

export default function Home({updateDecks, deckCount}){
  const [decks, setDecks] = useState([])
  useEffect(() => {
    const grabDecks = async () => {
      const decksFromApi = await listDecks;
      setDecks(decksFromApi)
    }
    grabDecks()
  }, [deckCount])


  return (
    <div>  
        <div className="row mx-auto w-75">
            <Link to="/decks/new" className="btn btn-secondary w-25 mb-3"><i className="fa fa-plus" aria-hidden="true"></i> Create Deck</Link>
        </div>
        <div className="row w-100 mx-auto flex-column align-items-center">
            {decks.map((deck) => <DisplayDecks key={deck.id} deck={deck} updateDecks={updateDecks} />)}
        </div>
      </div>
  )
}
