import React, {useEffect} from "react";
import {listDecks} from "../utils/api"
import {Link} from "react-router-dom";

export default function Home({decks, setDecks}){
  useEffect(()=>{
    async function loadDecks(){
      const decks = await listDecks();
      setDecks(decks);
    }
    loadDecks()
  }, [setDecks]);

  const listedDecks = decks && decks.map((deck)=> <Deck key={deck.id} deck={deck} />)

  return (
    <main className='container mb-3'>
      <Link to='decks/new'>
        <button type='button' className='btn btn-secondary btn-lg m-1'>
          <i className='fas fa-plus'></i> Create Deck
        </button>
      </Link>
      <section className='column'>{listedDecks}</section>
    </main>
  )
}
