import React, {useState, useEffect} from "react";
import {listDecks} from "../utils/api"
import {Link} from "react-router-dom";
import DisplayDecks from "../home/DisplayDecks";

export default function Home({updateDecks, deckCount}){
  const [decks, setDecks] = useState([])
  useEffect(() => {
    const grabDecks = async () => {
      const decksFromApi = await listDecks;
      setDecks(decksFromApi)
    }
    grabDecks()
  }, [deckCount])

  const deckList = decks.map((deck)=> <DisplayDecks key={deck.id} deck={deck} updateDecks={updateDecks}/>)

  return (
    <main className='container mb-3'>
      <Link to='/decks/new'>
        <button type='button' className='btn btn-secondary btn-lg m-1'>
          <i className='fas fa-plus'></i> Create Deck
        </button>
      </Link>
      <section className='column'>
      {deckList}
      </section>
    </main>
  )
}
