import React from 'react';
import {Link, useHistory, useParams} from "react-router-dom"
import {deleteCard} from "../utils/api"

export default function DisplayCard({card, deck}){
  const {id, front, back} = card
  const history = useHistory()
  const {deckId} = useParams()

  const deleteCardHandler = async () => {
    if (window.confirm("Are you sure you want to delete this card? You will not be able to recover it.")) {
      await deleteCard(deckId);
      history.go(0)
  }
}


return (
  <div className='card' key={id}>
        <div className='card-body row'>
          <div className='col-6'>
            <p className='text-muted'>{front}</p>
          </div>
          <div className='col-6'>
            <p className='text-muted'>{back}</p>
          </div>
          <br />
          <div className='ml-auto'>
            {/* Edit Card */}
            <Link to={`/decks/${deckId}/cards/${card.id}/edit`}>
              <button type='button' className='btn btn-secondary mr-2'>
                <i className='fas fa-pencil-alt'></i> Edit
              </button>
            </Link>
            {/* Delete Card */}
            <button
              type='button'
              className='btn btn-danger'
              title='Delete'
              onClick={deleteCardHandler}
            >
              <i className='fas fa-trash-alt'></i>
            </button>
          </div>
        </div>
      </div>
  )
}
