import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'

import { listDecks } from '../utils/api'
import Deck from '../decks/Deck'

export default function Home({ decks, setDecks }) {
  useEffect(() => {
    const abortController = new AbortController()

    listDecks(abortController.signal).then(setDecks)

    return () => abortController.abort()
  }, [setDecks])

  const deckCardList =
    decks && decks.map((deck) => <Deck key={deck.id} deck={deck} />)

  return (
    <main className='container mb-3'>
      <Link to='decks/new'>
        <button type='button' className='btn btn-secondary btn-lg m-1'>
          <i className='fas fa-plus'></i> Create Deck
        </button>
      </Link>
      <section className='column'>{deckCardList}</section>
    </main>
  )
}
