import React, {useState} from 'react'
import { Link, useHistory } from 'react-router-dom'

import { deleteDeck } from '../utils/api'

export default function Deck({ deck, deckList, setDeckList }) {
  const [updatedDecks, setUpdatedDecks] = useState([])
  const {id, name, description, cards} = deck;
  const history = useHistory()
  const numCards = cards.length

  const deleteHandler = async () => {
    if (window.confirm("Are you sure you want to delete this deck? You will not be able to recover it.")) {
      const response = await deleteDeck(id);
      setUpdatedDecks(response)
      history.go(0)
    } else {
        history.go(0)
    } 
}


  return (
    <div className='card m-1'>
      <div className='card-body' key={id}>
        <div className='d-flex w-100 justify-content-between'>
          <h5 className='card-title'>{name}</h5>
          <p className='text-muted'>{`${numCards} cards`}</p>
        </div>
        <p className='card-text'>{description}</p>
        <div className='buttons d-flex'>
          <Link
            to={`/decks/${deck.id}`}
            type='button'
            className='btn btn-secondary mr-2'
          >
            <i className='fas fa-eye'></i> View
          </Link>
          <Link
            to={`/decks/${deck.id}/study`}
            type='button'
            className='btn btn-primary'
          >
            <i className='fas fa-book'></i> Study
          </Link>
          <button
            type='button'
            className='btn btn-danger ml-auto'
            title='Delete'
            onClick={deleteHandler}
          >
            <i className='fas fa-trash-alt'></i>
          </button>
        </div>
      </div>
    </div>
  )
}
