import React, { useEffect, useState } from 'react'
import { useParams, Link, useHistory } from 'react-router-dom'
import { readDeck, deleteDeck, listCards } from '../utils/api'
import DisplayCard from '../cards/DisplayCard'

export default function Deck() {
  const [cards, setCards] = useState([])
  const [deck, setDeck] = useState([])
  const { deckId } = useParams()
  const history = useHistory()

  useEffect(() => {
    async function getDeck() {
      const displayDeck = await readDeck(deckId)
      setDisplayDeck(displayDeck)
    }
    getDeck()
  }, [deckId])

  useEffect(()=>{
    async function getCards(){
      const cards = await listCards(deckId)
      setCards(cards)
    }
    getCards()
  }, [deckId])

  const cardList = cards.map((card)=> <DisplayCard key={id} card={card} />)

  const deleteDeckHandler = async () => {
    if (window.confirm("Are you sure you want to delete this deck? You will not be able to recover it.")) {
      await deleteDeck(deckId);
      history.go(0)
    } 
}

  

 
      

  return (
    <div className='container'>
      <nav aria-label='breadcrumb'>
        <ol className='breadcrumb'>
          <li className='breadcrumb-item'>
            <Link to='/'>
              <i className='fas fa-home'></i> Home
            </Link>
          </li>
          <li className='breadcrumb-item active' aria-current='page'>
            {deck.name}
          </li>
        </ol>
      </nav>
      <div>
        <h3>{deck.name}</h3>
        <p>{deck.description}</p>
      </div>
      <div className='d-flex'>
        {/* Edit Deck */}
        <Link to={`/decks/${deck.id}/edit`}>
          <button type='button' className='btn btn-secondary mr-2'>
            <i className='fas fa-pencil-alt'></i> Edit
          </button>
        </Link>
        {/* Study Deck */}
        <Link to={`/decks/${deck.id}/study`}>
          <button type='button' className='btn btn-primary mr-2'>
            <i className='fas fa-book'></i> Study
          </button>
        </Link>
        {/* Add Cards */}
        <Link to={`/decks/${deck.id}/cards/new`}>
          <button type='button' className='btn btn-primary'>
            <i className='fas fa-plus'></i> Add Cards
          </button>
        </Link>
        {/* Delete Deck */}
        <button
          type='button'
          className='btn btn-danger ml-auto'
          title='Delete'
          onClick={deleteDeckHandler}
        >
          <i className='fas fa-trash-alt'></i>
        </button>
      </div>
      <br />
      <div className='mb-3'>
        <h2>Cards</h2>
        <section className='column'>{cardList}</section>
      </div>
    </div>
  )
}
